import React from "react";
import { useDispatch, useSelector } from "react-redux";
import CloseIcon from "@mui/icons-material/Close";
import { closeMenu } from "../../redux/menuReducer";
import { Link } from "react-router-dom";

const Overlay = () => {
  //const isMenuOpen = useSelector((state) => state.menu.open);
  const dispatch = useDispatch();

  const handleCloseMenu = () => {
    dispatch(closeMenu());
  };

  return (
    <div className="overlay absolute h-full w-full z-20  bg-black">
      <div class=" h-full absolute w-2/3 bg-black z-50 flex justify-between p-5 ">
        <div className="text-3xl text-white">
          <Link to="/">THE STORE</Link>
        </div>
        <div className="cursor-pointer z-50" onClick={handleCloseMenu}>
          <CloseIcon fontSize="large" style={{ color: "white" }} />
        </div>
      </div>
    </div>
  );
};

export default Overlay;
