import React, { useState } from "react";
import { Link } from "react-router-dom";

const Card = ({ item }) => {
  const [isHovered, setIsHovered] = useState(false); // State'i hover durumunu izlemek için kullanıyoruz.
  const url = process.env.REACT_APP_UPLOAD_URL;

  return (
    <Link to={`/product/${item.id}`}>
      <div
        className="max-w-3xl flex flex-col gap-3 relative"
        onMouseEnter={() => setIsHovered(true)} // Fare üzerine gelince hover durumunu true yapar
        onMouseLeave={() => setIsHovered(false)} // Fare ayrılınca hover durumunu false yapar
      >
        <div className="image h-72 flex justify-between">
          <img
            src={url + item.attributes.img.data.attributes.url}
            alt=""
            className={`h-full object-cover cursor-pointer ${
              isHovered ? "hidden" : "" // Hover olunca ilk resmi gizler
            }`}
          />
          <img
            src={url + item.attributes.img2.data.attributes.url}
            alt=""
            className={`h-full object-cover absolute top-0 right-0 ${
              isHovered ? "block" : "hidden" // Hover olunca ikinci resmi gösterir
            }`}
          />
        </div>

        <p>{item.attributes.title}</p>
        <div>
          <span className="line-through text-gray-500 mr-2">
            ${item.oldPrice || item?.attributes.price + 20}
          </span>
          {item?.attributes.price}$
        </div>
        {item.attributes?.isNew && (
          <div className="absolute left-1.5 top-1.5 bg-white text-xs text-teal-600 p-1">
            New Season
          </div>
        )}
      </div>
    </Link>
  );
};

export default Card;
