import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import CloseIcon from "@mui/icons-material/Close";
import { openFilter } from "../../redux/filterResponsiveReducer";

const FilterOverlay = () => {
  const isOpenFilter = useSelector((state) => state.openFilter.openFilter);
  const dispatch = useDispatch();
  const [anime, setAnime] = useState("");

  //   useEffect(() => {
  //     if (isOpenFilter === true) {
  //       setAnime("open");
  //     } else {
  //       setAnime("close");
  //     }
  //   }, [isOpenFilter]);

  //   const closeFilter = () => {
  //     setAnime("close");
  //     setTimeout(() => {
  //       dispatch(openFilter());
  //       setAnime("");
  //     }, 300);
  //   };

  return (
    <>
      {isOpenFilter && (
        <div className="fixed z-30 h-full w-full">
          <div
            className={`bg-black h-[40vh] opacity-20 duration-0 ${
              isOpenFilter ? "translate-y-100" : "-translate-y-full"
            }`}
            onClick={() => dispatch(openFilter())}
          ></div>
          <div
            className={`fixed z-30 w-full h-[60vh] bg-[#FFFFFF] p-5 transition-transform transform ${
              isOpenFilter ? "translate-y-0" : "translate-y-full"
            }`}
          >
            <div className="flex justify-between">
              FilterOverlay
              <div className="cursor-pointer z-50" onClick={openFilter}>
                <CloseIcon fontSize="large" style={{ color: "gray" }} />
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default FilterOverlay;
