const Overlay = () => {
  const isMenuOpen = useSelector((state) => state.menu.open);
  const dispatch = useDispatch();

  const handleCloseMenu = () => {
    dispatch(closeMenu());
  };

  const menuAnimationClass = isMenuOpen ? "overlay-open" : "overlay-closed";

  return (
    <div
      className={`overlay absolute h-full w-2/3 bg-black z-30 flex justify-between p-6 ${menuAnimationClass}`}
    >
      <div className="text-2xl text-white">
        <Link to="/">THE STORE</Link>
      </div>
      <div className="cursor-pointer z-50" onClick={handleCloseMenu}>
        <CloseIcon fontSize="large" style={{ color: "white" }} />
      </div>
    </div>
  );
};
