import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import CloseIcon from "@mui/icons-material/Close";
import { openFilter } from "../../redux/filterResponsiveReducer";

const FilterOverlay = () => {
  const isOpenFilter = useSelector((state) => state.openFilter.openFilter);
  const dispatch = useDispatch();
  const [anime, setAnime] = useState("");

  const openFilterOverlay = () => {
    setAnime("open");
  };

  const closeFilterOverlay = () => {
    setAnime("close");
    dispatch(openFilter());
  };

  return (
    <>
      {isOpenFilter && (
        <div
          className={`fixed z-30 h-full w-full ${
            anime === "open" ? "filter-overlay-open" : ""
          }`}
        >
          <div
            className="bg-black h-[40vh] opacity-20"
            onClick={closeFilterOverlay}
          ></div>
          <div
            className={`fixed z-30 w-full h-[60vh] bg-[#FFFFFF] p-5 transition-transform transform ${
              anime === "open" ? "translate-y-0" : "translate-y-full"
            }`}
          >
            <div className="flex justify-between">
              FilterOverlay
              <div className="cursor-pointer z-50" onClick={closeFilterOverlay}>
                <CloseIcon fontSize="large" style={{ color: "gray" }} />
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default FilterOverlay;
