import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import useFetch from "../../hooks/useFetch";
import "./Products.css";
import FilterSide from "./FilterSide";
import ItemSide from "./ItemSide";

const Products = () => {
  const { id, page } = useParams();
  const catId = parseInt(id);
  const [maxPrice, setMaxPrice] = useState(1000);
  const [sort, setSort] = useState(null);
  const [selectedSubCats, setSelectedSubCats] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [loadingSubCategories, setLoadingSubCategories] = useState(true);

  // Sub Categories'leri çekmek için useFetch kullanın (örneğin, /sub-categories?filters[...])

  // API'den verileri çekmek için useFetch veya diğer uygun hook'u kullanın
  useFetch(`/sub-categories?[filters][categories][id][$eq]=${catId}`)
    .then((response) => {
      // Verileri alın ve state'i güncelleyin
      setSubCategories(response.data);
      setLoadingSubCategories(false);
    })
    .catch((error) => {
      // Hata durumunda işlem yapın
      console.error("Sub Categories yüklenirken bir hata oluştu:", error);
    });

  return (
    <div className="flex mx-5 mt-8 gap-32">
      <FilterSide
        data={subCategories}
        maxPrice={maxPrice}
        setMaxPrice={setMaxPrice}
        sort={sort}
        setSort={setSort}
        selectedSubCats={selectedSubCats}
        setSelectedSubCats={setSelectedSubCats}
      />
      <ItemSide
        catId={catId}
        maxPrice={maxPrice}
        sort={sort}
        selectedSubCats={selectedSubCats}
        page={page}
      />
    </div>
  );
};

export default Products;
