import React from "react";
import { useDispatch, useSelector } from "react-redux";
import CloseIcon from "@mui/icons-material/Close";

const FilterOverlay = () => {
  const isOpenFilter = useSelector((state) => state.openFilter.openFilter);
  const dispatch = useDispatch();
  return (
    <>
      {isOpenFilter && (
        <div className="fixed z-30 h-full w-full ">
          <div className="bg-black h-[40vh] opacity-20"></div>
          <div className="fixed z-30 bottom-0 left-0 right-0 w-full h-[60vh] bg-red-500">
            FilterOverlay
          </div>
          <div
            className="cursor-pointer z-50"
            onClick={() => dispatch(openFilter())}
          >
            <CloseIcon fontSize="large" className="close-icon" />
          </div>
        </div>
      )}
    </>
  );
};

export default FilterOverlay;
