import React from "react";
import { Link } from "react-router-dom";

const Card = ({ item }) => {
  return (
    <Link to={`/product/${item.id}`}>
      <div className=" max-w-3xl h-full flex flex-col gap-3 relative  ">
        <img src={item.img} alt="" className=" h-full object-cover" />
        <p>{item.attributes.title}</p>
        <div>
          <span className="line-through text-gray-500 mr-2">
            ${item.oldPrice}
          </span>
          {item.attributes.price}$
        </div>
        {item.isNew && (
          <div className="absolute left-1.5 top-1.5 bg-white text-xs text-teal-600 p-1">
            New Season
          </div>
        )}
      </div>
    </Link>
  );
};

export default Card;
