import React from "react";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";

const Cart = () => {
  const data = [
    {
      id: 1,
      img: "https://images.pexels.com/photos/1972115/pexels-photo-1972115.jpeg",
      title: "Long Sleeve Graphic T-shirt",
      desc: "Long Sleeve Graphic T-shirt",
      isNew: true,
      oldPrice: 19,
      price: 12,
    },
    {
      id: 2,
      img: "https://images.pexels.com/photos/1759622/pexels-photo-1759622.jpeg",
      title: "Coat",
      desc: "Coat",
      isNew: true,
      oldPrice: 19,
      price: 12,
    },
  ];

  return (
    <div className="cart absolute top-20 right-5 z-50 bg-white p-5 shadow-lg">
      <h1 className="mb-7 text-gray-500 font-normal text-2xl">
        Product in your cart
      </h1>
      {data?.map((item) => (
        <div className="item flex items-center gap-5 mb-7" key={item.id}>
          <div className="w-full flex items-center gap-5 ">
            <img src={item.img} alt="" className="w-20 h-24 object-cover" />
            <div className="details">
              <h1 className=" text-lg font-medium">{item.title}</h1>
              <p className="text-gray-500 font-medium mb-2 text-sm">
                {item.desc?.substring(0, 100)}
              </p>
              <div className="price text-blue-500">1 x ${item.price}</div>
            </div>
          </div>
          <DeleteOutlineIcon className="delete text-red-500 cursor-pointer" />
        </div>
      ))}
      <div className="total flex justify-between font-medium text-lg mb-5">
        <span>SUBTOTAL</span>
        <span>$123</span>
      </div>
      <button className="w-44 py-3 bg-blue-500 text-white  flex justify-center items-center gap-3 cursor-pointer font-medium text-xs mb-3 hover:bg-blue-600 duration-200">
        PROCEED TO CHECKOUT
      </button>
      <span className="reset text-red-500 text-xs cursor-pointer hover:text-red-600">
        Reset Cart
      </span>
    </div>
  );
};

export default Cart;
