import React, { useState } from "react";
import { Link } from "react-router-dom";

const Card = ({ item }) => {
  const [showImg, setShowImg] = useState(false);
  const url = process.env.REACT_APP_UPLOAD_URL;

  return (
    <Link to={`/product/${item.id}`}>
      <div
        className=" max-w-3xl flex flex-col gap-3 relative bg-red-400"
        onMouseEnter={() => setShowImg(true)}
        onMouseLeave={() => setShowImg(false)}
      >
        {item?.attributes.isNew && (
          <div className="absolute left-1.5 top-1.5 bg-white text-xs text-teal-600 p-1 z-50">
            New Season
          </div>
        )}
        <img
          src={url + item.attributes.img.data.attributes.url}
          alt=""
          className=" h-full object-cover"
        />
        <img
          src={url + item.attributes.img2.data.attributes.url}
          alt=""
          className={` h-full object-cover absolute top-0  ${
            !showImg ? "hidden" : ""
          } `}
        />
      </div>
      <p>{item.attributes.title}</p>
      <div>
        <span className="line-through text-gray-500 mr-2">
          ${item.oldPrice || item?.attributes.price + 20}
        </span>
        {item?.attributes.price}$
      </div>
    </Link>
  );
};

export default Card;
