import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import CloseIcon from "@mui/icons-material/Close";
import { toggle } from "../../redux/menuReducer";
import { Link } from "react-router-dom";

const Overlay = () => {
  const isMenuOpen = useSelector((state) => state.menu.open);
  const dispatch = useDispatch();

  const handleCloseMenu = () => {
    dispatch(toggle());
  };

  useEffect(() => {
    // Boş dependency array, sadece başlangıçta çalışmasını sağlar.
    // isOpen değişmediği sürece bu efekt çalışmayacak.
  }, []);

  return (
    <div
      className={`fixed h-full w-[60vw] bg-black z-30 p-6 transition-transform duration-500 ${
        isMenuOpen ? "left-0" : "-left-[60vw]"
      }`}
    >
      <div className="text-2xl text-white">
        <Link to="/">THE STORE</Link>
      </div>
      <div className="cursor-pointer z-50" onClick={handleCloseMenu}>
        <CloseIcon fontSize="large" style={{ color: "white" }} />
      </div>
    </div>
  );
};

export default Overlay;
