import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import CloseIcon from "@mui/icons-material/Close";
import { closeMenu } from "../../redux/menuReducer";
import { Link } from "react-router-dom";
import "./Overlay.css";

const Overlay = () => {
  const isMenuOpen = useSelector((state) => state.menu.open);
  const dispatch = useDispatch();

  const handleCloseMenu = () => {
    dispatch(closeMenu());
  };

  const [menuAnimation, setMenuAnimation] = useState("");

  useEffect(() => {
    setMenuAnimation(isMenuOpen ? "open" : "");
  }, [isMenuOpen]);

  const menuStyles = {
    transform: isMenuOpen ? "translateX(0)" : "translateX(-100%)",
    transition: "transform 0.7s ease-in-out",
  };

  return (
    <div
      className="overlay absolute h-full w-2/3 bg-black z-30 flex justify-between p-6"
      style={{ menuStyles }}
    >
      <div className="text-2xl text-white">
        <Link to="/">THE STORE</Link>
      </div>
      <div className="cursor-pointer z-50" onClick={handleCloseMenu}>
        <CloseIcon fontSize="large" style={{ color: "white" }} />
      </div>
    </div>
  );
};

export default Overlay;
