import React, { useState, useEffect } from "react";
import Card from "../Card/Card";

const List = ({ catId, maxPrice, subCats, sort }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Strapi API'ye istek göndermek için fetch kullanabilirsiniz.
    // sort parametresini de isteğe dahil edin.
    fetch(
      `/strapi-api-endpoint?catId=${catId}&maxPrice=${maxPrice}&subCats=${subCats}&sort=${sort}`
    )
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("API Hatası:", error);
        setLoading(false);
      });
  }, [catId, maxPrice, subCats, sort]);

  return (
    <div className="max-w-5xl grid grid-cols-4 gap-8">
      {loading
        ? "Yükleniyor"
        : data?.map((item) => <Card item={item} key={item.id} />)}
    </div>
  );
};

export default List;
