import { useState, useEffect } from "react";
// ... (other imports)

const Product = () => {
  const id = useParams().id;

  const [quantity, setQuantity] = useState(1);
  const { data, loading, error } = useFetch(`/products/${id}?populate=*`);

  const imgUrl =
    process.env.REACT_APP_UPLOAD_URL +
    data?.attributes?.img?.data?.attributes?.url;
  const img2Url =
    process.env.REACT_APP_UPLOAD_URL +
    data?.attributes?.img2?.data?.attributes?.url;

  const [selectedImg, setSelectedImg] = useState(""); // Initialize with an empty string

  // Set the selected image to the first image URL when data is available
  useEffect(() => {
    if (!loading && imgUrl) {
      setSelectedImg(imgUrl);
    }
  }, [loading, imgUrl]);

  const handleClick = (url) => {
    setSelectedImg(url);
  };

  return (
    <div className="px-5 py-12 flex gap-12">
      {loading ? (
        "loading"
      ) : (
        <>
          <div className="left flex flex-1 gap-5">
            <div className="images flex-1 ">
              <img
                src={imgUrl}
                alt=""
                onClick={() => handleClick(imgUrl)}
                className="w-full h-36 object-cover cursor-pointer mb-2"
              />
              <img
                src={img2Url}
                alt=""
                onClick={() => handleClick(img2Url)}
                className="w-full h-36 object-cover cursor-pointer mb-2"
              />
            </div>
            <div className="mainImg flex-[5]">
              <img
                src={selectedImg}
                alt=""
                className="w-full max-h-[650px] object-cover"
              />
            </div>
          </div>
          {/* ... (rest of your component) */}
        </>
      )}
    </div>
  );
};

export default Product;
