import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import CloseIcon from "@mui/icons-material/Close";
import { closeMenu } from "../../redux/menuReducer";
import { Link } from "react-router-dom";
import "./Overlay.css";

const Overlay = () => {
  const isMenuOpen = useSelector((state) => state.menu.open);
  const dispatch = useDispatch();

  const handleCloseMenu = () => {
    dispatch(closeMenu());
  };

  const [menuAnimation, setMenuAnimation] = useState("");

  useEffect(() => {
    if (isMenuOpen) {
      setMenuAnimation("open");
    } else {
      // Menü kapanırken animasyonu başlat
      setMenuAnimation("close");

      // Animasyon tamamlandığında animasyon sınıfını temizle
      const timeout = setTimeout(() => {
        setMenuAnimation("");
      }, 500); // Bu süreyi menü kapanma süresine ayarlayın
      return () => clearTimeout(timeout);
    }
  }, [isMenuOpen]);

  return (
    <div
      className={`absolute h-full w-[60vw]  bg-black z-30 flex justify-between p-6 transition-all duration-500 ${
        menuAnimation === "open" ? "translate-x-0" : "translate-x-full"
      }`}
    >
      <div className="text-2xl text-white">
        <Link to="/">THE STORE</Link>
      </div>

      <div className="cursor-pointer z-50" onClick={handleCloseMenu}>
        <CloseIcon fontSize="large" style={{ color: "white" }} />
      </div>
    </div>
  );
};

export default Overlay;
