import React from "react";
import { useParams, useLocation } from "react-router-dom";
import useFetch from "../../hooks/useFetch";
import "./Products.css";
import FilterSide from "./FilterSide";
import ItemSide from "./ItemSide";

const Products = () => {
  const catId = parseInt(useParams().id);
  const [maxPrice, setMaxPrice] = React.useState(1000);
  const [minPrice, setMinPrice] = React.useState(0);
  const [sort, setSort] = React.useState(null);
  const [selectedSubCats, setSelectedSubCats] = React.useState([]);
  const location = useLocation();
  const isSale = new URLSearchParams(location.search).get("isSale");

  const { data, loading, error } = useFetch(
    `/sub-categories?[filters][categories][id][$eq]=${catId}`
  );

  return (
    <div className="flex mx-5 mt-8 gap-32">
      <FilterSide
        data={data}
        catId={catId}
        maxPrice={maxPrice}
        setMaxPrice={setMaxPrice}
        setMinPrice={setMinPrice}
        sort={sort}
        setSort={setSort}
        selectedSubCats={selectedSubCats}
        setSelectedSubCats={setSelectedSubCats}
      />
      <ItemSide
        catId={catId}
        maxPrice={maxPrice}
        minPrice={minPrice}
        sort={sort}
        selectedSubCats={selectedSubCats}
        page={isSale === "true" ? "sale" : ""} // "Sale" filtresi etkinleştirilmişse "sale" olarak ayarla
      />
    </div>
  );
};

export default Products;
