import React, { useState } from "react";
import { BrowserRouter, Route, Routes, Outlet } from "react-router-dom";
import Home from "./pages/Home/Home";
import Product from "./pages/Product/Product";
import Men from "./pages/Men/Men";
import Women from "./pages/Women/Women";
import Navbar from "./components/Navbar/Navbar";
import Footer from "./components/Footer/Footer";
import { useSelector } from "react-redux";
import "./App.css";

const Layout = () => {
  const isMenuOpen = useSelector((state) => state.menu.open);
  const [isScrollLocked, setScrollLocked] = useState(false);

  useEffect(() => {
    if (isMenuOpen) {
      // Menü açıldığında
      document.body.classList.add("scroll-locked");
      setScrollLocked(true);
    } else {
      // Menü kapandığında
      document.body.classList.remove("scroll-locked");
      setScrollLocked(false);
    }
  }, [isMenuOpen]);

  return (
    <>
      {isMenuOpen && (
        <div class="overlay h-full w-full bg-black z-30 fixed overflow-hidden "></div>
      )}
      <div className={`app ${isScrollLocked ? "scroll-locked" : ""}`}>
        <Navbar />
        <Outlet />
        <Footer />
      </div>
    </>
  );
};

function App() {
  return (
    <div>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Layout />}>
            <Route index element={<Home />} />
            <Route path="products/women/:id" element={<Women />} />
            <Route path="products/men/:id" element={<Men />} />
            <Route path="product/:id" element={<Product />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
