import React from "react";
import Card from "../Card/Card";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";

const FeaturedProducts = ({ type }) => {
  // const data = [
  //   {
  //     id: 1,
  //     img: "https://images.pexels.com/photos/1972115/pexels-photo-1972115.jpeg",
  //     title: "Long Sleeve Graphic T-shirt",
  //     isNew: true,
  //     oldPrice: 19,
  //     price: 12,
  //   },
  //   {
  //     id: 2,
  //     img: "https://images.pexels.com/photos/1759622/pexels-photo-1759622.jpeg",
  //     title: "Coat",
  //     isNew: true,
  //     oldPrice: 19,
  //     price: 12,
  //   },
  //   {
  //     id: 3,
  //     img: "https://images.pexels.com/photos/1457983/pexels-photo-1457983.jpeg",
  //     title: "Skirt",
  //     isNew: false,
  //     oldPrice: 19,
  //     price: 12,
  //   },
  //   {
  //     id: 4,
  //     img: "https://images.pexels.com/photos/2065200/pexels-photo-2065200.jpeg",
  //     title: "Hat",
  //     isNew: false,
  //     oldPrice: 19,
  //     price: 12,
  //   },
  // ];

  const token = "REACT_APP_API_TOKEN";

  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(
          process.env.REACT_APP_API_URL + "/products?populate=*",
          {
            headers: {
              Authorization: `bearer  + ${token}`,
            },
          }
        );
        console.log(res.data.data);
        setData(res.data.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, []);

  return (
    <div className="max-w-full space-y-10 mx-20 text-sm my-24">
      <div>
        <h1 className="text-3xl font-bold">{type} Products</h1>
      </div>
      <div className="max-w-5xl mx-auto flex gap-8 bg-blue-500">
        {data?.map((item) => (
          <Card item={item} key={item.id} />
        ))}
      </div>
    </div>
  );
};

export default FeaturedProducts;
