import { useState } from "react";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import BalanceIcon from "@mui/icons-material/Balance";
import { useParams } from "react-router-dom";
import useFetch from "../../hooks/useFetch";

const Product = () => {
  const id = useParams().id;
  const [selectedImg, setSelectedImg] = useState(""); // Seçili resmin URL'sini saklayacak state
  const [quantity, setQuantity] = useState(1);

  const { data, loading, error } = useFetch(`/products/${id}?populate=*`);

  // Resim tıklanınca selectedImg'i güncelle
  const handleImageClick = (imgUrl) => {
    setSelectedImg(imgUrl);
  };

  return (
    <div className="px-5 py-12 flex gap-12">
      {loading ? (
        "Yükleniyor..."
      ) : (
        <>
          <div className="left flex flex-1 gap-5">
            <div className="images flex-1 ">
              <img
                src={
                  process.env.REACT_APP_UPLOAD_URL +
                  data?.attributes?.img?.data?.attributes?.url
                }
                alt=""
                onClick={() =>
                  handleImageClick(
                    process.env.REACT_APP_UPLOAD_URL +
                      data?.attributes?.img?.data?.attributes?.url
                  )
                }
                className="w-full h-36 object-cover cursor-pointer mb-2"
              />
              <img
                src={
                  process.env.REACT_APP_UPLOAD_URL +
                  data?.attributes?.img2?.data?.attributes?.url
                }
                alt=""
                onClick={() =>
                  handleImageClick(
                    process.env.REACT_APP_UPLOAD_URL +
                      data?.attributes?.img2?.data?.attributes?.url
                  )
                }
                className="w-full h-36 object-cover cursor-pointer mb-2"
              />
            </div>
            <div className="mainImg flex-[5]">
              <img
                src={selectedImg} // Seçili resmi burada göster
                alt=""
                className="w-full max-h-[650px] object-cover"
              />
            </div>
          </div>
          <div className="right flex-1 flex flex-col gap-7">
            <h1>{data?.attributes?.title}</h1>
            <span className="text-lg text-blue-500 font-semibold">$199</span>
            <p className="  font-light text-justify">
              {data?.attributes?.description}
            </p>
            <div className="quantity flex justify-between items-center gap-2 w-36">
              <button
                onClick={() =>
                  setQuantity((prev) => (prev === 1 ? 1 : prev - 1))
                }
                className="w-12 h-12 flex items-center justify-center cursor-pointer border-none bg-gray-100"
              >
                -
              </button>
              <span className="mx-1">{quantity}</span>
              <button
                onClick={() => setQuantity((prev) => prev + 1)}
                className="w-12 h-12 flex items-center justify-center cursor-pointer border-none bg-gray-100"
              >
                +
              </button>
            </div>
            <button className="add w-52 py-3 bg-blue-500 text-white flex justify-center items-center gap-3 cursor-pointer font-medium text-sm">
              <AddShoppingCartIcon fontSize="small" /> SEPETE EKLE
            </button>
            <div className="links flex gap-5">
              <div className="item flex item-center gap-2 text-blue-500 text-sm">
                <FavoriteBorderIcon fontSize="small" />
                İSTENİLENLERE EKLE
              </div>
              <div className="item flex item-center gap-2 text-blue-500 text-sm">
                <BalanceIcon fontSize="small" /> KARŞILAŞTIR
              </div>
            </div>
            <div className="info flex flex-col gap-2 text-gray-500 text-xs mt-4">
              <span>Üretici: Polo</span>
              <span>Ürün Türü: T-Shirt</span>
              <span>Etiket: T-Shirt, Kadın, Üst</span>
            </div>
            <hr className="w-52 border border-solid border-gray-100 " />
            <div className="info flex flex-col gap-2 text-gray-500 text-xs mt-4">
              <span>AÇIKLAMA</span>
              <hr className="w-52 border border-solid border-gray-100" />
              <span>DAHA FAZLA BİLGİ</span>
              <hr className="w-52 border border-solid border-gray-100" />
              <span>SSS</span>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Product;
