import React, { useState } from "react";

const FilterPrice = ({ data, setSort, setMaxPrice }) => {
  const radios = [
    {
      id: 1,
      name: "$0 - $100",
    },
    {
      id: 2,
      name: "$100 - $500",
    },
    {
      id: 3,
      name: "$500 - $1000",
    },
  ];

  const [filteredData, setFilteredData] = useState(data); // Filtrelenmiş verileri saklamak için bir state tanımlayın

  const handler = (event) => {
    const targetId = parseInt(event.target.id); // id'yi bir tamsayıya dönüştür

    let maxPrice = null; // Maksimum fiyatı sıfırla

    switch (targetId) {
      case 1:
        maxPrice = 100;
        break;
      case 2:
        maxPrice = 500;
        break;
      case 3:
        maxPrice = 1000;
        break;
      default:
        break;
    }

    // Verileri filtrelemek için bir işlev kullanın
    const filtered = data.filter((item) => {
      if (maxPrice === null) {
        return true; // Fiyat filtresi yoksa tüm verileri göster
      }
      return item.attributes.price <= maxPrice;
    });

    // Filtrelenmiş verileri güncelleyin
    setFilteredData(filtered);

    // Maksimum fiyatı ayarla
    setMaxPrice(maxPrice);
  };

  return (
    <div className="flex flex-col gap-1 text-sm">
      {radios?.map((radio) => (
        <label htmlFor={radio.id} key={radio.id} className="flex gap-1">
          <input
            type="radio"
            name="price"
            id={radio.id.toString()} // id'yi bir dizeye dönüştür
            onChange={handler}
          />
          {radio.name}
        </label>
      ))}
      <ul>
        {filteredData.map((item) => (
          <li key={item.id}>
            {item.name} - ${item.attributes.price}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FilterPrice;
