import { createSlice } from "@reduxjs/toolkit";

// İlk başlangıç durumu
const initialCartState = {
  products: [],
};

// İkinci başlangıç durumu
const initialFavState = {
  productFav: [],
};

// İlk dilim
export const cartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    addToCart: (state, action) => {
      const item = state.products.find((item) => item.id === action.payload.id);
      if (item) {
        item.quantity += action.payload.quantity;
      } else {
        state.products.push(action.payload);
      }
    },
    removeItem: (state, action) => {
      state.products = state.products.filter(
        (item) => item.id !== action.payload
      );
    },
    resetCart: (state) => {
      state.products = [];
    },
  },
});

// İkinci dilim
export const favSlice = createSlice({
  name: "favorites",
  initialState: initialFavState,
  reducers: {
    addToFav: (state, action) => {
      const item = state.productFav.find(
        (item) => item.id === action.payload.id
      );
      if (item) {
        item.quantity += action.payload.quantity;
      } else {
        state.productFav.push(action.payload);
      }
    },
  },
});

export const { addToCart, removeItem, resetCart } = cartSlice.actions;
export const { addToFav } = favSlice.actions;

export default {
  cartReducer: cartSlice.reducer,
  favReducer: favSlice.reducer,
};
