import MenuIcon from "@mui/icons-material/Menu";
import CloseIcon from "@mui/icons-material/Close";
import { useDispatch, useSelector } from "react-redux";

// import { toggle } from "../../redux/menuReducer";
import { open } from "../../redux/menuReducer";
import { close } from "../../redux/menuReducer";

const Menu = () => {
  const isMenuOpen = useSelector((state) => state.menu.open);
  const dispatch = useDispatch();

  const handleOpenMenu = () => {
    dispatch(open());
  };

  const handleCloseMenu = () => {
    dispatch(close());
  };

  return (
    <div className="relative md:hidden p-5 ">
      {!isMenuOpen && (
        <div
          className="cursor-pointer absolute top-0 right-0"
          onClick={handleOpenMenu}
        >
          <MenuIcon fontSize="large" />
        </div>
      )}
      {isMenuOpen && (
        <div className="cursor-pointer z-50" onClick={handleCloseMenu}>
          <CloseIcon fontSize="large" />
        </div>
      )}
    </div>
  );
};

export default Menu;
